import * as React from 'react';
import Backdrop from '@mui/material/Backdrop';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Fade from '@mui/material/Fade';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { useState } from 'react';
import axios from 'axios';
import { useEffect } from 'react';
import { img_500 , unavailable,
    unavailableLandscape, } from '../config/config';
    
import YouTubeIcon from '@mui/icons-material/YouTube';
import "./ContentModal.css";



const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  bgcolor: "#39445a",
  border: '1px solid #282c34',
  width:"70%",
  height:"60%",
  borderRadius:10,
  color:"white",
  boxShadow:"0px 1px 5px black",
  padding:5,
  display:"flex",
  flexDirection:"column"
  
  
};

export default function ContentModal({children,media_type,id}) {
  const [open, setOpen] = useState(false);
  const [content,setContent] = useState();
  const [video,setVideo] = useState()
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const fetchData = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/${media_type}/${id}?api_key=67fefbaa38331d1b02df78df2fb49196&language=en-US`
    );

    setContent(data);
    
  };

  const fetchVideo = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/${media_type}/${id}/videos?api_key=67fefbaa38331d1b02df78df2fb49196&language=en-US`
    );

    setVideo(data.results[0]?.key);
  };

  useEffect(() => {
     fetchData();
     fetchVideo();
  }, [])

  return (<>
    <div>
      <Button onClick={handleOpen} className="media">{children}</Button>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open} className="ContentModal">
          {content && (<Box sx={style}>
        
                 
                <img
                  src={
                    content.poster_path
                      ? `${img_500}/${content.poster_path}`
                      : unavailable
                  }
                  alt={content.name || content.title}
                  className="ContentModal__portrait"
                />
                 <img
                  src={
                    content.backdrop_path
                      ? `${img_500}/${content.backdrop_path}`
                      : unavailableLandscape
                  }
                  alt={content.name || content.title}
                  className="ContentModal__landscape"
                />
                 <span className="ContentModal__title">
                    {content.name || content.title} (
                    {(
                      content.first_air_date ||
                      content.release_date ||
                      "-----"
                    ).substring(0, 4)}
                    )
                  </span>
                  {content.tagline && (
                    <i className="tagline">{content.tagline}</i>
                  )}
                  <span className="ContentModal__description">
                    {content.overview}
                  </span>
                  <Button
                    variant="contained"
                    startIcon={<YouTubeIcon/>}
                    color="secondary"
                    target="__blank"
                    href={`https://www.youtube.com/watch?v=${video}`}
                  >
                    Watch the Trailer
                  </Button>
               
                

             <div/>
            
          </Box>)}
        </Fade>
      </Modal>
    </div>
  </>);
}
