import axios from 'axios'
import React, { useEffect } from 'react'

const Genres = ({
    selectedGenres,
    setSelectedGenres,
    genres,
    setGenres,
    type,
    setPage
}) => {
    const fetchGenres = async () => {
        const {data} = await axios.get(
            `https://api.themoviedb.org/genre/${type}/all/day?api_key=67fefbaa38331d1b02df78df2fb49196&${setPage}&language=en-US`
        )
        setGenres(data.genres);
    };
    console.log(genres);

    useEffect(() => {
        fetchGenres();

        return () => {
            setGenres({});
        }

    },[]);
    return (
        <div>
            genres
        </div>
    )
}

export default Genres
